<?phpglobal $wp_query,$st_textdomain;$sidebar_position=st_get_option('sidebar_position','no');?><?php get_header(); ?>    <section class="blog-page-title">        <div class="container">            <div class="row text-center">                <div class="col-lg-12 section-title wow flipInX animated">                    <h1>                        <small><?php bloginfo('name')?></small>                        <br>                        <strong><?php printf( __( 'All posts by %s', $st_textdomain ), '<span class="vcard"><a class="url fn n" href="' . esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ) . '" title="' . esc_attr( get_the_author() ) . '" rel="me">' . get_the_author() . '</a></span>' ); ?></strong>                    </h1>                </div>            </div>        </div>    </section>    <section class="section-single">        <div class="container">            <div class="row">                <?php                if($sidebar_position=="no"){                    $page_class="single-post-wrapper clearfix col-lg-12 col-md-12";                }else{                    $page_class="single-post-wrapper clearfix  col-lg-8";                }                ?>                <?php                if($sidebar_position=="left"){                    $page_class .=' pull-right';                }                ?>                <div class="<?php echo $page_class; ?>">                    <?php echo get_template_part('loop')?>                </div><!-- single-post-wrapper -->                <?php if($sidebar_position!="no"){ ?>                    <div id="sidebar" class="col-lg-4">                        <?php dynamic_sidebar('single-post'); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->    </section><!-- end section-single --><?php get_footer(); ?>