<?phpglobal $st_show_banner,$st_hasBanner,$st_textdomain;$header_position=1;if(is_page()){    $header_position=get_post_meta(get_the_ID(),'_cmb_menu_style',true);    if(!$header_position)    {        $header_position=1;    }}$class="";if($header_position == 2 and $st_show_banner ){    $class='header-top';}if($header_position == 3){    $class='header-bottom';}if(!$st_hasBanner){    $class='';}?><section id="fixed-navbar" class="<?php echo $class ?>">    <nav class="navbar navbar-default navbar-static-top" role="navigation">        <div class="container">            <!-- Brand and toggle get grouped for better mobile display -->            <div class="navbar-header">                <button type="button" class="navbar-toggle" data-toggle="collapse" data-target="#main-nav">                    <span class="sr-only"><?php echo __('Toggle navigation',$st_textdomain) ?></span>                    <span class="icon-bar"></span>                    <span class="icon-bar"></span>                    <span class="icon-bar"></span>                </button>                <a class="navbar-brand" href="<?php echo home_url(); ?>" title="">                    <?php                    $logo=st_get_option('logo');                    if($logo and isset($logo['url']) and $logo['url'] ){ ?>                        <img src="<?php echo $logo['url'] ?>" alt="<?php bloginfo('name'); ?>">                    <?php                    }else                    {                        echo "<h3>".get_bloginfo('name')."</h3>";                    }?>                </a>            </div>            <!-- Collect the nav links, forms, and other content for toggling -->            <?php            if(has_nav_menu('primary'))            {                wp_nav_menu(array('theme_location'=>'primary',                    'walker'=>new wp_bootstrap_navwalker(),                    'container_class'=>'collapse navbar-collapse',                    'container_id'=>'main-nav',                    'menu_class'=>'nav navbar-nav  navbar-right'));            }else{                echo "<p class='navbar-right'><b>";                echo __('Make your menu at Appearance => Menus ','wunderkind');                echo "</b></p>";            }            ?>        </div><!-- /.container -->    </nav></section>