<?phpglobal $wp_query,$st_textdomain;$content_position=get_post_meta(get_the_ID(), '_cmb_content_position', true);//Set Post Viewst_set_post_view();?><?php$class='ajax-request';if(!st_is_ajax()){    $class='';    get_header();}?>    <section class="blog-page-title <?php echo $class?>">        <div class="container">            <div class="row text-center">                <div class="col-lg-12 section-title wow flipInX animated">                    <h1>                        <small><?php bloginfo('name')?></small>                        <br>                        <strong><?php the_title()?></strong>                    </h1>                    <p class="lead">                        <?php echo do_shortcode(get_post_meta(get_the_ID(),'_cmb_project_sub_title',true))?>                    </p>                </div>            </div>        </div>    </section>    <section class="section-single" style="padding-bottom: 40px">        <div class="container">            <div class="row">                <?php while(have_posts()) : the_post(); ?>                    <?php                    if($content_position == "fullwidth"){                        $page_class="single-post-wrapper clearfix col-lg-12 col-md-12";                    }else{                        $page_class="single-post-wrapper clearfix  col-lg-8";                    }                    ?>                    <?php                    if($content_position == "right"){                        $page_class .=' pull-right';                    }                    ?>                    <div class="<?php echo $page_class; ?>">                        <div <?php post_class()?> >                            <?php                                echo get_template_part('content',get_post_format());                                if($url=get_post_meta(get_the_ID(),'_cmb_project_url',true))                                {                                    echo "<a href='{$url}' class='btn btn-primary'>".__('Visit Site',$st_textdomain)."</a>";                                }                            ?>                            <?php if(!st_is_ajax()) { ?>                            <div class="pager">                                <?php                                echo previous_post_link('%link','<button type="button" class="btn btn-primary" style="float:left"><i class="icon ion-arrow-left-b"></i> '.__('Older',$st_textdomain).'</button>');                                echo next_post_link('%link','<button type="button" class="btn btn-primary" style="float:right">'.__('Newer',$st_textdomain).' <i class="icon ion-arrow-right-b"></i></button>');                                ?>                            </div>                            <?php //comments_template(); ?>                            <?php  }?>                        </div>                    </div><!-- single-post-wrapper -->                    <?php                    if($content_position == 'fullwidth')                    {                        ?>                                                    <div class="col-sm-6 blog-widget-container">                                <h4><?php echo __('Project Description',$st_textdomain)?></h4>                                <p>                                    <?php echo nl2br(get_post_meta(get_the_ID(),'_cmb_desc',true)) ?>                                </p>                            </div>                            <div class="col-sm-6">                                <div class="blog-widget-container">                                    <h4><?php echo __('Project Details',$st_textdomain)?></h4>                                    <h5><?php echo __('Date',$st_textdomain)?>: <small><?php echo get_post_meta(get_the_ID(),'_cmb_project_date',true)?></small></h5>                                    <h5><?php echo __('Client',$st_textdomain)?>: <small><?php echo get_post_meta(get_the_ID(),'_cmb_project_client',true)?></small></h5>                                </div>                                    <?php                                        $tags=get_the_terms(get_the_ID(),'portfolio_tag');                                        if(!isset($tags->errors) and !empty($tags))                                        {                                            ?>                                            <div>                                                <h4><?php echo __('Tags',$st_textdomain)?></h4>                                                <ul class="tags-list">                                                    <?php foreach($tags as $key=>$value){                                                       echo "<li><a href='".get_term_link($value,'portfolio_tag')."'>$value->name</a> </li>";                                                    }?>                                                </ul>                                            </div>                                            <?php                                        }                                    ?>                            </div>                                                <?php                    }else{ ?>                        <div class="col-lg-4">                            <div class="blog-widget-container">                                <h4><?php echo __('Project Description',$st_textdomain)?></h4>                                <p>                                    <?php echo nl2br(get_post_meta(get_the_ID(),'_cmb_desc',true)) ?>                                </p>                            </div>                            <div class="blog-widget-container">                                    <h4><?php echo __('Project Details',$st_textdomain)?></h4>                                    <h5><?php echo __('Date',$st_textdomain)?>: <small><?php echo get_post_meta(get_the_ID(),'_cmb_project_date',true)?></small></h5>                                    <h5><?php echo __('Client',$st_textdomain)?>: <small><?php echo get_post_meta(get_the_ID(),'_cmb_project_client',true)?></small></h5>                                </div>                                <?php                                $tags=get_the_terms(get_the_ID(),'portfolio_tag');                                if(!isset($tags->errors) and !empty($tags))                                {                                    ?>                                    <div>                                        <h4><?php echo __('Tags',$st_textdomain)?></h4>                                        <ul class="tags-list">                                            <?php foreach($tags as $key=>$value){                                                echo "<li><a href='".get_term_link($value,'portfolio_tag')."'>$value->name</a> </li>";                                            }?>                                        </ul>                                    </div>                                <?php                                }                                ?>                            </div>                        <?php                    }                    ?>                <?php endwhile; ?>            </div>        </div><!-- end container -->    </section><!-- end section-single --><?phpif(!st_is_ajax()){    get_footer();}else{    ?>    <?php}?>