<?phpglobal $wp_query,$st_textdomain;$sidebar_position=get_post_meta(get_the_ID(), '_cmb_sidebar_position', true);st_set_post_view();if(!$sidebar_position) $sidebar_position='no';?><?php get_header(); ?>    <section class="blog-page-title">        <div class="container">            <div class="row text-center">                <div class="col-lg-12 section-title wow flipInX animated">                    <h1>                        <small><?php bloginfo('name')?></small>                        <br>                        <strong><?php the_title()?></strong>                    </h1>                    <p class="lead">                        <?php echo do_shortcode(get_post_meta(get_the_ID(),'_cmb_page_sub_title',true))?>                    </p>                </div>            </div>        </div>    </section>    <section class="section-single">        <div class="container">            <div class="row">                <?php while(have_posts()) : the_post(); ?>                    <?php                    if($sidebar_position == "no"){                        $page_class="single-post-wrapper clearfix col-lg-12 col-md-12";                    }else{                        $page_class="single-post-wrapper clearfix  col-lg-8";                    }                    ?>                    <?php                    if($sidebar_position == "left"){                        $page_class .=' pull-right';                    }                    ?>                    <div class="<?php echo $page_class; ?>">                        <div <?php post_class()?> >                            <?php                            get_template_part( 'content', get_post_format() );                            the_tags();                            ?>                            <div class="blog-author">                                <hr>                                <h3 class="heading"><span><?php echo __('About the Author',$st_textdomain)?></span></h3>                                <div class="author-img">                                    <?php echo get_avatar(get_the_author_meta('ID'),'130')?>                                </div>                                <p><?php the_author_meta('description'); ?>                                                                    </p>                                <p>                                    <a href="<?php echo get_user_meta(get_the_author_meta('ID'),'facebook_url',true)?>"><i class="fa  fa-facebook-square fa-2x"></i></a>                                    <a href="<?php echo get_user_meta(get_the_author_meta('ID'),'twitter_url',true)?>"><i class="fa  fa-twitter-square fa-2x"></i></a>                                    <a href="<?php echo get_user_meta(get_the_author_meta('ID'),'google_plus_url',true)?>"><i class="fa  fa-google-plus-square fa-2x" ></i></a>                                </p>                                                                <div class="clear">&nbsp;</div>                                <hr>                            </div>                            <?php comments_template(); ?>                            <div class="pager">                                <?php                               echo previous_post_link('%link','<button type="button" class="btn btn-primary" style="float:left"><i class="icon ion-arrow-left-b"></i> '.__('Older',$st_textdomain).'</button>');                                echo next_post_link('%link','<button type="button" class="btn btn-primary" style="float:right">'.__('Newer',$st_textdomain).' <i class="icon ion-arrow-right-b"></i></button>');                                ?>                            </div>                        </div>                    </div><!-- single-post-wrapper -->                <?php endwhile; ?>                <?php if($sidebar_position!="no"){ ?>                    <div id="sidebar" class="col-lg-4">                        <?php dynamic_sidebar('single-post'); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->    </section><!-- end section-single --><?php get_footer(); ?>