<?phpglobal $st_textdomain;//Show Gallery$gallery = get_post_gallery( get_the_ID(), false );//Content position of portfolio$content_position=get_post_meta(get_the_ID(), '_cmb_content_position', true);/* Loop through all the image and output them one by one */if($gallery and !empty($gallery) and isset($gallery['ids'])){    $ids=explode(',',$gallery['ids']);    if(!empty($ids))    {        echo '<div class="flexslider project-img"><ul class="slides">';        foreach($ids as $key=>$value)        {            if($content_position=='fullwidth')            {                $image=wp_get_attachment_image_src($value,array(1140,591,'bfi_thumb'=>true));            }else            {                $image=wp_get_attachment_image_src($value,array(750,389,'bfi_thumb'=>true));            }            ?>            <li>                <img class="img-responsive" src="<?php echo $image[0]; ?>" />            </li>        <?php        }        echo '</ul></div>';    }}if(get_post_type()!='portfolio'){?>    <h2 class="post-title"><a href="<?php echo the_permalink()?>"><?php the_title()?></a></h2>    <p><?php echo  __('by',$st_textdomain)?> <a href="<?php echo get_author_posts_url( get_the_author_meta( 'ID' ) ); ?>"><?php the_author() ?></a> <small><?php echo get_user_meta(get_the_author_meta('ID'),'job',true) ?></small></p>    <p><span class="icon ion-ios7-clock"></span> <?php echo __('Posted on',$st_textdomain)?> <?php echo the_date('F d, Y \a\t H:i A') ?></p><?php}$content = st_strip_shortcode_gallery( get_the_content() );$content = str_replace( ']]>', ']]&gt;', apply_filters( 'the_content', $content ) );echo $content;?>