<?php$enable=false;global $st_hasBanner;$st_hasBanner=false;if(is_page()){    $enable=get_post_meta(get_the_ID(),'_cmb_enable_banner',true);    $header_position=get_post_meta(get_the_ID(),'_cmb_menu_style',true);    if(!$header_position) $header_position=1;}?><?phpif(is_page() and $enable){    $slides_img=get_post_meta(get_the_ID(),'_cmb_banner_images',true);    $slides_text=get_post_meta(get_the_ID(),'_cmb_banner_texts',true);    $video_url=get_post_meta(get_the_ID(),'_cmb_video_url',true);    $disable_slider_text=get_post_meta(get_the_ID(),'_cmb_banner_disable_slider_text',true );    if(!empty($slides_img) or !empty($slides_text) or !$video_url )    {        $st_hasBanner=true;        if($header_position==2 )        {            echo get_template_part('menu');        }        $duration=get_post_meta(get_the_ID(),'_cmb_banner_duration',true);        $fade=get_post_meta(get_the_ID(),'_cmb_banner_fade',true);        if(!$fade)        {            $fade=500;        }        if(!$duration)        {            $duration=4000;        }        $wunderkind=array();        $css='';        if(!empty($slides_img) and count($slides_img)>1)        {            foreach($slides_img as $key=>$value)            {                $wunderkind['slides'][]=$value;            }        }        if(!empty($slides_img) and count($slides_img)==1){            reset($slides_img);            $css='background-image:url('.current($slides_img).');';        }        $wunderkind['fade']=$fade;        $wunderkind['duration']=$duration;        if($disable_slider_text){            $wunderkind['disable_slider_text']=1;        }        if($video_url)        {            $wunderkind['video_version']=1;        }else        {            $wunderkind['video_version']=0;        }        //If video url is define, no image so        //We update, on mobile, show image instead of video        //if(!$video_url)        //{            wp_localize_script('themescript','wunderkind',$wunderkind);        //}        ?>        <!-- Start Home-Video -->        <section style='<?php echo $css ?>' class='st_parallax' id="home-pattern" data-stellar-background-ratio="0.5" data-stellar-vertical-offset="">            <div class="parallax-overlay"></div>            <?php if(isset($video_url) and $video_url) { ?>                <a id="video" class="player" data-append='#home-pattern' data-videourl='<?php echo trim($video_url) ?>' data-property="{videoURL:'<?php echo trim($video_url) ?>',containment:'#home-pattern', showControls:true, autoPlay:true, loop:true, mute:true, startAt:0, opacity:1, quality:'default'}"></a>            <?php  } ?>            <div class="home-container text-center">                <?php $data_duration = get_post_meta(get_the_ID(),'_cmb_banner_duration',true); ?>                <div class="home-title liquid-slider" id="slider-home" data-duration="<?php echo esc_attr($data_duration); ?>" style="z-index:99;">                    <?php if($slides_text){                        echo wpb_js_remove_wpautop($slides_text);                    } ?>                </div>            </div>            <div class="home-bottom">                <div class="container text-center">                    <div class="move">                        <a href="#about" class="fa fa-chevron-down"></a>                        <div class="dots"></div>                    </div>                </div>            </div>            <?php            if($header_position==3)            {                echo get_template_part('menu');            }            ?>        </section>        <!--End Home--><?php        if($header_position==1)        {            echo get_template_part('menu');        }    }else{        echo get_template_part('menu');    }}else{    echo get_template_part('menu');}