<?phpglobal $wp_query;$sidebar_position=st_get_option('home_sidebar_position','no');?><?php get_header(); ?>    <section class="section-single" style="padding-top: 40px">        <div class="container">            <div class="row">                <?php                if($sidebar_position=="no"){                    $page_class="single-post-wrapper clearfix col-lg-12 col-md-12";                }else{                    $page_class="single-post-wrapper clearfix  col-lg-8";                }                ?>                <?php                if($sidebar_position=="left"){                    $page_class .=' pull-right';                }                ?>                <div class="<?php echo $page_class; ?>">                    <?php echo get_template_part('loop')?>                </div><!-- single-post-wrapper -->                <?php if($sidebar_position!="no"){ ?>                    <div id="sidebar" class="col-lg-4">                        <?php dynamic_sidebar('single-post'); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->    </section><!-- end section-single --><?php get_footer(); ?>