<?phpglobal $st_textdomain;if(have_posts()){    while(have_posts())    {        the_post();        echo "<div ";        post_class();        echo ">";        if($video=get_post_meta(get_the_ID(),'_cmb_embed_video',true))        {        ?>            <div class="fluid-width-video-wrapper post-img" style="padding-top:56.25%;">                <?php                // echo  do_shortcode('[embed ]'.$video.'[/embed]');                $embed_code =  wp_oembed_get($video, array('height'=>750));                echo $embed_code;                ?>            </div>        <?php        }        //Show Gallery        $gallery = get_post_gallery( get_the_ID(), false );        /* Loop through all the image and output them one by one */        if($gallery and !empty($gallery))        {            $ids=explode(',',$gallery['ids']);            if(!empty($ids))            {                echo '<div class="flexslider project-img"><ul class="slides">';                foreach($ids as $key=>$value)                {                    $size=array(750,389,'bfi_thumb'=>true);                    $image=wp_get_attachment_image_src($value,$size);                    ?>                    <li>                        <img class="img-responsive" height="389" width="750" src="<?php echo $image[0]; ?>" />                    </li>                <?php                }                echo '</ul></div>';            }        }        ?>        <h2 class="post-title"><a href="<?php echo the_permalink()?>"><?php the_title()?></a></h2>        <p><?php echo __('by',$st_textdomain)?> <a rel="author" href="<?php echo esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ); ?>"><?php the_author() ?></a> <small><?php echo get_user_meta(get_the_author_meta('ID'),'job',true) ?></small></p>        <p><span class="icon ion-ios7-clock"></span> <?php echo __('Posted on',$st_textdomain)?> <?php echo get_the_date('F d, Y \a\t H:i A') ?></p>        <p><?php the_excerpt()?></p>        <a class="btn btn-primary" href="<?php the_permalink()?>"><?php echo __('Continue Reading',$st_textdomain) ?></a>        <br><br><br>    <?php        echo "</div>";    }}else{}    st_blog_paging();?>