<?php//This paramenter use on vc_row, because this page have already div.container, so no need .container on what row that VC buidglobal $st_remove_container;$st_remove_container=true;/*Template Name: Blog Template*/global $wp_query; ?><?php get_header(); ?>    <section class="blog-page-title">        <div class="container">            <div class="row text-center">                <div class="col-lg-12 section-title wow flipInX animated">                    <h1>                        <small><?php bloginfo('name')?></small>                        <br>                        <strong><?php the_title()?></strong>                    </h1>                    <p class="lead">                        <?php echo do_shortcode(get_post_meta(get_the_ID(),'_cmb_page_sub_title',true))?>                    </p>                </div>            </div>        </div>    </section>    <section class="section-single">        <div class="container">            <div class="row">                                    <?php                    if(get_post_meta(get_the_ID(), '_cmb_sidebar_position', true) == "no"){                        $page_class="single-post-wrapper clearfix col-lg-12 col-md-12";                    }else{                        $page_class="single-post-wrapper clearfix  col-lg-8";                    }                    ?>                    <?php                    if(get_post_meta(get_the_ID(), '_cmb_sidebar_position', true) == "left"){                        $page_class .=' pull-right';                    }                    ?>                    <div class="<?php echo $page_class; ?>">                        <?php $arg=array('post_type'=>'post','paged'=>get_query_var('paged' ));                            query_posts($arg );                            get_template_part('loop');                            wp_reset_query();;                            ?>                                                    <?php while(have_posts()) : the_post(); ?>                        <?php the_content(); ?>                        <?php endwhile; ?>                        <?php wp_link_pages(); ?>                    </div><!-- single-post-wrapper -->                <?php if(get_post_meta(get_the_ID(), '_cmb_sidebar_position', true) != "no"){ ?>                    <div id="sidebar" class="col-lg-4">                        <?php dynamic_sidebar('single-post'); ?>                    </div><!-- end sidebar -->                <?php } ?>            </div>        </div><!-- end container -->    </section><!-- end section-single --><?php get_footer(); ?>